                  <button type="submit" class="btn btn-primary" onClick={this.check8}>8</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check9}>9</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check10}>10</button>&nbsp;&nbsp;
                  <br></br><text>Answer = {rand} </text>
                  </div>
    <div style={hidden}>
    <form  onSubmit={this.handleSubmit} onKeyPress={event => {
    if (event.which === 13 /* Enter */) {
      event.preventDefault();
      }
    }}>
     <div className="row">
              <div className="col-8">
                <div className="form-row">
                <div className="col-4">
                  <input type="text" name="name" className="form-control" placeholder="name" onChange={this.handleChange} value={this.state.name}/></div>
                  <div className="col-12">
                    <textarea type="text" name="description" className="form-control" placeholder="description" onChange={this.handleChange} value={this.state.description}/>
                  </div>
                  <br></br><br></br>
                  <div className="col-12">
                  <textarea type="text" name="wrong" className="form-control" placeholder="wrong" onChange={this.handleChange} value={this.state.wrong}/>
                  </div>
                  <br></br><br></br><br></br>
                  <div className="col">
                  <button type="submit" class="btn btn-primary">Save</button>&nbsp;&nbsp;
                  </div>
                </div>
              </div>
          </div>
    </form>
    </div>
   <div>
   <center><b>Leader Board</b><br></br></center>
   <table className="table table-hover">
                    <tr className="thead-light">
                      <th width="18%">name</th>
                      <th width="20%">description</th>
                      <th width="52%">wrong</th>
                      <th width="5%">Edit</th>
                      <th width="5%">Delete</th>
                    </tr>
                    {
                        this.state.users.map((user) => {
                          return (
                              <tr>
                                <td>{user.name}</td>
                                <td>{user.description}</td>
                                <td>{user.wrong}</td>
                                <td><button className="btn btn-warning btn-md" onClick={() => this.handleUpdate(user.user_id,user.name,user.description,user.wrong)}>Edit</button></td>
                                <td><button className="btn btn-danger btn-md" onClick={() => this.removeuser(user.user_id)}>Delete</button></td>
                              </tr>
                          )
                        })
                    }
                </table></div>
   </div>
  );
}
}

export default App;
