import React, { Component } from 'react';
import firebase from './firebase'
var rand =  1;//Math.floor(Math.random() * Math.floor(10));

class App extends Component {

  constructor(){
     super();
     this.state = {
        users:[],
        user_id:'',
        description:'',
        name:'',
        address:'',
        shown: true,

     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)

  }


  componentDidMount(){
    const usersRef = firebase.database().ref('users');
    usersRef.on('value',(snapshot) => {
        let users = snapshot.val();
        let newState = [];
        for(let item in users){
          newState.push({
              user_id:item,
              description:users[item].description,
              name:users[item].name,
              address:users[item].address
          })
        }
        this.setState({
          users:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();
    if(this.state.description==''){
      alert("Please choose item before users.");
    }
    else if(this.state.name==''){
      alert("Please input Your Name.");
    }
    else if(this.state.address==''){
      alert("Please input Your Address.");
    }
    else{
    if(this.state.user_id != ''){
      return this.updateItem();
    }
    const usersRef = firebase.database().ref('users')
    const item = {
       description : this.state.description,
       name : this.state.name,
       address : this.state.address
    }
    usersRef.push(item)
    this.setState({
       user_id:'',
       description:'',
       name:'',
       address:''
    })
  }
 }

  handleUpdate = (user_id = null , description = null , name = null , address = null) => {
    this.setState({user_id,description,name,address})
  }

  updateItem(){

      var obj = { description:this.state.description,name:this.state.name,address:this.state.address }

      const usersRef = firebase.database().ref('/users')

      usersRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        description:'',
        name:'',
        address:''
      })

  }

  removeItem(itemId){
    const usersRef = firebase.database().ref('/users');
    usersRef.child(itemId).remove();
 }
 toggleModal() {    
  this.setState((prev, props) => {
    const newState = !prev.modalState;

    return { modalState: newState };
  });
}
