}
render() {
  var shown = {
    display: this.state.shown ? "block" : "none"
  };

  var hidden = {
    display: this.state.shown ? "none" : "block"
  }
  return (
    <div className="app">
    <div style={shown}><button type="submit" class="btn btn-primary" onClick={this.check1.bind(this)}>1</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check2}>2</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check3}>3</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check4}>4</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check5}>5</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check6}>6</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check7}>7</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check8}>8</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check9}>9</button>&nbsp;&nbsp;
                  <button type="submit" class="btn btn-primary" onClick={this.check10}>10</button>&nbsp;&nbsp;
                  <text>{rand}</text>
                  <table className="table table-hover">

                    {
                        this.state.orders.map((item) => {
                          return (
                              <tr>
                                <td>{item.name}</td>
                                <td>{item.description}</td>
                                <td>{item.address}</td>
                                <td><button classdescription="btn btn-warning btn-md" onClick={() => this.handleUpdate(item.user_id,item.name,item.description,item.address)}>Edit</button></td>
                                <td><button className="btn btn-danger btn-md" onClick={() => this.removeItem(item.user_id)}>Delete</button></td>
                              </tr>
                          )
                        })
                    }
                </table></div>
    <div style={hidden}>
    <form  onSubmit={this.handleSubmit} onKeyPress={event => {
    if (event.which === 13 /* Enter */) {
      event.preventDefault();
      }
    }}>
     <div className="row">
              <div className="col-8">
                <div className="form-row">
                <div className="col-4">
                  <input type="text" name="name" className="form-control" placeholder="name" onChange={this.handleChange} value={this.state.name}/></div>
                  <div className="col-12">
                    <textarea type="text" name="description" className="form-control" placeholder="description" onChange={this.handleChange} value={this.state.description}/>
                  </div>
                  <br></br><br></br>
                  <div className="col-12">
                  <textarea type="text" name="address" className="form-control" placeholder="address" onChange={this.handleChange} value={this.state.address}/>
                  </div>
                  <br></br><br></br><br></br>
                  <div className="col">
                  <button type="submit" class="btn btn-primary">Save</button>&nbsp;&nbsp;
                  </div>
                </div>
              </div>
          </div>
    </form>
    </div>

   </div>
  );
}
}

export default App;
