import React, { Component } from 'react';
import firebase from './firebase'
import './App.css';

var rand =1+(Math.floor(Math.random() * Math.floor(9))); //1-10
var count = 0;
class App extends Component {

  constructor(){
     super();
     this.state = {
        users:[],
        user_id:'',
        about:'',
        name:'',
        wrong:'',
        shown: true   

     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)
     this.check1 = this.check1.bind(this)
     this.check2 = this.check2.bind(this)
     this.check3 = this.check3.bind(this)
     this.check4 = this.check4.bind(this)
     this.check5 = this.check5.bind(this)
     this.check6 = this.check6.bind(this)
     this.check7 = this.check7.bind(this)
     this.check8 = this.check8.bind(this)
     this.check9 = this.check9.bind(this)
     this.check10 = this.check10.bind(this)
     this.hit = this.hit.bind(this)

  }  

  componentDidMount(){
    const usersRef = firebase.database().ref('users');
    usersRef.on('value',(snapshot) => {
        let users = snapshot.val();
        let newState = [];
        for(let user in users){
          newState.push({
              user_id:user,
              about:users[user].about,
              name:users[user].name,
              wrong:users[user].wrong
          })
        }
        this.setState({
          users:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();
    if(this.state.about==''){
      alert("Please choose user before users.");
    }
    else if(this.state.name==''){
      alert("Please input Your Name.");
    }
    else{
      if(this.state.user_id != ''){
        return this.updateuser();
      }
      const usersRef = firebase.database().ref('users')
      const user = {
        about : this.state.about,
        name : this.state.name,
        wrong : this.state.wrong
      }
      usersRef.push(user)
      this.setState({
        user_id:'',
        about:'',
        name:'',
        wrong:''
      })
      this.state.shown = !this.state.shown
      rand =1+(Math.floor(Math.random() * Math.floor(9)));
    }
  }

  handleUpdate = (user_id = null , about = null , name = null , wrong = null) => {
    this.setState({user_id,about,name,wrong})
  }

  updateuser(){

      var obj = { about:this.state.about,name:this.state.name,wrong:this.state.wrong }

      const usersRef = firebase.database().ref('/users')

      usersRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        about:'',
        name:'',
        wrong:''
      })

  }

  removeuser(userId){
    const usersRef = firebase.database().ref('/users');
    usersRef.child(userId).remove();
  }
  toggleModal() {    
    this.setState((prev, props) => {
    const newState = !prev.modalState;

    return { modalState: newState };
    });
  }
