import React, { Component } from 'react';
import firebase from './firebase'

class App extends Component {

  constructor(){
     super();
     this.state = {
        orders:[],
        user_id:'',
        item_name:'',
        price:'',
        name:'',
        address:'',
        shown: true,
        lip:true,
        eye:true,
        cus:true
     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)

  }


  componentDidMount(){
    const ordersRef = firebase.database().ref('orders');
    ordersRef.on('value',(snapshot) => {
        let orders = snapshot.val();
        let newState = [];
        for(let item in orders){
          newState.push({
              user_id:item,
              item_name:orders[item].item_name,
              price:orders[item].price,
              name:orders[item].name,
              address:orders[item].address
          })
        }
        this.setState({
          orders:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();

    if(this.state.user_id != ''){
      return this.updateItem();
    }

    const ordersRef = firebase.database().ref('orders')
    const item = {
       item_name : this.state.item_name,
       price : this.state.price,
       name : this.state.name,
       address : this.state.address
    }
    ordersRef.push(item)
    this.setState({
       user_id:'',
       price:'',
       item_name:'',
       name:'',
       address:''
    })
 }

  handleUpdate = (user_id = null ,price = null, item_name = null , name = null , address = null) => {
    this.setState({user_id,item_name,price,name,address})
  }

  updateItem(){

      var obj = { item_name:this.state.item_name,price:this.state.price,name:this.state.name,address:this.state.address }

      const ordersRef = firebase.database().ref('/orders')

      ordersRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        item_name:'',
        price:'',
        name:'',
        address:''
      })

  }

  removeItem(itemId){
    const ordersRef = firebase.database().ref('/orders');
    ordersRef.child(itemId).remove();
 }
 toggleModal() {    
  this.setState((prev, props) => {
    const newState = !prev.modalState;

    return { modalState: newState };
  });
}
admin() {
  var password = window.prompt("Password")
  if(password=='admin'){
  this.setState({
    shown: !this.state.shown
  })}
  else{
    alert("wrong password");
  };
}
showLip(){
  this.setState({
    lip: false,
    eye: true,
    cus: true
  })
}
showEye(){
  this.setState({
    lip: true,
    eye: false,
    cus: true
  })
}
showCus(){
  this.setState({
    lip: true,
    eye: true,
    cus: false
  })
}
  render() {
    var shown = {
			display: this.state.shown ? "block" : "none"
		};

		var hidden = {
			display: this.state.shown ? "none" : "block"
    }

    var hidelip={
      display: this.state.lip ? "none" : "block"
    }
    var hideeye={
      display: this.state.eye ? "none" : "block"
    }
    var hidecus={
      display: this.state.cus ? "none" : "block"
    }
    return (
      <div className="app">
      <div class="navbar">
      <table className="table table-sm table-bordered">
       <tr className="thead-dark"><th width="33%"><a href="#" onClick={this.showLip.bind(this)}>Lipstick</a></th>
       <th width="33%"><a href="#" onClick={this.showEye.bind(this)}>EyePalett</a></th>
       <th width="33%"><a href="#" onClick={this.showCus.bind(this)}>Cusion</a></th></tr>

      </table></div>
      <div className="container" style={{marginTop:70}}>
      <form  onSubmit={this.handleSubmit} onKeyPress={event => {
      if (event.which === 13 /* Enter */) {
        event.preventDefault();
        }
      }}>
            <div className="row">
                <div className="col-8">
                  <div className="form-row">
                    <div className="col-4">
                      <input type="text" name="name" className="form-control" placeholder="name" onChange={this.handleChange} value={this.state.name}/>
    </div>
                    <div className="col-6">
                      <input type="text" name="address" className="form-control" placeholder="address" onChange={this.handleChange} value={this.state.address}/>
                    </div>
                    <div className="col">
                    <table className="table table-sm table-bordered">
                    <tr className="thead-dark">
                    <th width="50%"><button type="submit" class="btn btn-primary">Order</button></th>
                    <th width="50%"><button type="button" class="btn btn-primary" onClick={()=>alert("ในตระกร้ามีสินค้าดังนี้"+this.state.item_name)}>Cart</button>   </th>
                    </tr>
                    </table>
                    </div>
                  </div>
                </div>
            </div>
            <table style={ hidelip } className="table table-sm table-bordered">
                    <tr className="thead-dark">
                    <th width="25%"><img src={require('./pic/1.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img><button>xxxxxxx</button></th>
                    <th width="25%"><img src={require('./pic/2.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Orange'}></img></th>
                    <th width="25%"><img src={require('./pic/3.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/4.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>

                    </tr>
                    <tr className="thead-dark">

                    <th width="25%"><img src={require('./pic/5.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/6.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/7.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/8.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    </tr>
                    </table>
           <table style={ hideeye } className="table table-sm table-bordered">
                    <tr className="thead-dark">
                    <th width="25%"><img src={require('./pic/9.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/10.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Orange'}></img></th>
                    <th width="25%"><img src={require('./pic/11.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/12.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>

                    </tr>
                    <tr className="thead-dark">

                    <th width="25%"><img src={require('./pic/13.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/14.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/15.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/16.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    </tr>
                    </table>
            <table style={ hidecus } className="table table-sm table-bordered">
                    <tr className="thead-dark">
                    <th width="25%"><img src={require('./pic/17.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/18.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Orange'}></img></th>
                    <th width="25%"><img src={require('./pic/19.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/20.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>

                    </tr>
                    <tr className="thead-dark">

                    <th width="25%"><img src={require('./pic/21.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/22.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/23.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    <th width="25%"><img src={require('./pic/24.jpg')}  onClick={() => this.state.item_name=this.state.item_name+'\nLipstick Red   '}></img></th>
                    </tr>
                    </table>
          </form>
        <hr/>
        <button onClick={this.admin.bind(this)}>Admin</button>
              <table style={ hidden } className="table table-sm table-bordered">
                    <tr className="thead-dark">
                      <th width="12%">item_name</th>
                      <th width="20%">name</th>
                      <th width="58%">address</th>
                      <th width="5%">Edit</th>
                      <th width="5%">Delete</th>
                    </tr>
                    {
                        this.state.orders.map((item) => {
                          return (
                              <tr>
                                <td>{item.item_name}</td>
                                <td>{item.name}</td>
                                <td>{item.address}</td>
                                <td><button className="btn btn-warning btn-sm" onClick={() => this.handleUpdate(item.user_id,item.item_name,item.name,item.address)}>Edit</button></td>
                                <td><button className="btn btn-danger btn-sm" onClick={() => this.removeItem(item.user_id)}>Delete</button></td>
                              </tr>
                          )
                        })
                    }
                </table>
          </div>
      </div>
    );
  }
}

export default App;
