import React, { Component } from 'react';
import firebase from './firebase'
var rand =  1;//Math.floor(Math.random() * Math.floor(10));
class App extends Component {

  constructor(){
     super();
     this.state = {
        Winner:[],
        user_id:'',
        name:'',
        Description:'',
        Guideline:'',
        shown: true,
     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)
  }


  componentDidMount(){
    const WinnerRef = firebase.database().ref('Winner');
    WinnerRef.on('value',(snapshot) => {
        let Winner = snapshot.val();
        let newState = [];
        for(let item in Winner){
          newState.push({
              user_id:item,
              name:Winner[item].name,
              Description:Winner[item].Description,
              Guideline:Winner[item].Guideline
          })
        }
        this.setState({
          Winner:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.Description]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();
    if(this.state.name==''){
      alert("Please input Your name");
    }
    else if(this.state.Description==''){
      alert("Please input Your Description.");
    }
    else if(this.state.Guideline==''){
      alert("Please input Your Guideline.");
    }
    else{
    if(this.state.user_id != ''){
      return this.updateItem();
    }
    const WinnerRef = firebase.database().ref('Winner')
    const item = {
       name : this.state.name,
       Description : this.state.Description,
       Guideline : this.state.Guideline
    }
    WinnerRef.push(item)
    this.setState({
       user_id:'',
       name:'',
       Description:'',
       Guideline:''
    })
  }
 }

  handleUpdate = (user_id = null , name = null , Description = null , Guideline = null) => {
    this.setState({user_id,name,Description,Guideline})
  }

  updateItem(){

      var obj = { name:this.state.name,Description:this.state.Description,Guideline:this.state.Guideline }

      const WinnerRef = firebase.database().ref('/Winner')

      WinnerRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        name:'',
        Description:'',
        Guideline:''
      })

  }


check1(){
