  render() {
    var shown = {
			display: this.state.shown ? "block" : "none"
		};

		var hidden = {
			display: this.state.shown ? "none" : "block"
    }
    return (
      <div classDescription="app">
      <div style={shown}><button type="submit" class="btn btn-primary" onClick={this.check1.bind(this)}>1</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check2}>2</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check3}>3</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check4}>4</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check5}>5</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check6}>6</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check7}>7</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check8}>8</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check9}>9</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary" onClick={this.check10}>10</button>&nbsp;&nbsp;
                    <text>{rand}</text></div>
      <div style={hidden}>
      <form  onSubmit={this.handleSubmit} onKeyPress={event => {
      if (event.which === 13 /* Enter */) {
        event.preventDefault();
        }
      }}>
      <input type="text" name="name" className="form-control" placeholder="name" onChange={this.handleChange} value={this.state.name}/>
      <input type="text" name="Description" className="form-control" placeholder="Description" onChange={this.handleChange} value={this.state.Description}/>
      <input type="text" name="Guideline" className="form-control" placeholder="Guideline" onChange={this.handleChange} value={this.state.Guideline}/>
      <button type="submit" class="btn btn-primary">Save</button>&nbsp;&nbsp;
      </form>
      </div>

     </div>
    );
  }
}

export default App;
