import React, { Component } from 'react';
import firebase from './firebase'


class App extends Component {

  constructor(){
     super();
     this.state = {
        orders:[],
        user_id:'',
        item_name:'',
        name:'',
        address:'',
        shown: true,
        lip:true,
        eye:true,
        cus:true,
        home:false
     }
     this.handleChange = this.handleChange.bind(this)
     this.handleUpdate = this.handleUpdate.bind(this)
     this.handleSubmit = this.handleSubmit.bind(this)

  }


  componentDidMount(){
    const ordersRef = firebase.database().ref('orders');
    ordersRef.on('value',(snapshot) => {
        let orders = snapshot.val();
        let newState = [];
        for(let item in orders){
          newState.push({
              user_id:item,
              item_name:orders[item].item_name,
              name:orders[item].name,
              address:orders[item].address
          })
        }
        this.setState({
          orders:newState
        })
    })
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    })
  }


  handleSubmit(e){
    e.preventDefault();
    if(this.state.item_name==''){
      alert("Please choose item before orders.");
    }
    else if(this.state.name==''){
      alert("Please input Your Name.");
    }
    else if(this.state.address==''){
      alert("Please input Your Address.");
    }
    else{
    if(this.state.user_id != ''){
      return this.updateItem();
    }
    const ordersRef = firebase.database().ref('orders')
    const item = {
       item_name : this.state.item_name,
       name : this.state.name,
       address : this.state.address
    }
    ordersRef.push(item)
    this.setState({
       user_id:'',
       item_name:'',
       name:'',
       address:''
    })
  }
 }

  handleUpdate = (user_id = null , item_name = null , name = null , address = null) => {
    this.setState({user_id,item_name,name,address})
  }

  updateItem(){

      var obj = { item_name:this.state.item_name,name:this.state.name,address:this.state.address }

      const ordersRef = firebase.database().ref('/orders')

      ordersRef.child(this.state.user_id).update(obj);

      this.setState({
        user_id:'',
        item_name:'',
        name:'',
        address:''
      })

  }

  removeItem(itemId){
    const ordersRef = firebase.database().ref('/orders');
    ordersRef.child(itemId).remove();
 }
 toggleModal() {    
  this.setState((prev, props) => {
    const newState = !prev.modalState;

    return { modalState: newState };
  });
}
admin() {
  var password = window.prompt("Password")
  if(password=='admin'){
  this.setState({
    shown: !this.state.shown
  })}
  else{
    alert("wrong password");
  };
}
showLip(){
  this.setState({
    lip: false,
    eye: true,
    cus: true,
    home:true
  })
}
showEye(){
  this.setState({
    lip: true,
    eye: false,
    cus: true,
    home:true
  })
}
showCus(){
  this.setState({
    lip: true,
    eye: true,
    cus: false,
    home:true
  })
}
showHome(){
  this.setState({
    lip: true,
    eye: true,
    cus: true,
    home:false
  })
}
  render() {
   var min = 1;
   var max = 100;
   var rand =  min + (Math.random() * (max-min))/1;
    var shown = {
			display: this.state.shown ? "block" : "none"
		};

		var hidden = {
			display: this.state.shown ? "none" : "block"
    }

    var hidelip={
      display: this.state.lip ? "none" : "block"
    }
    var hideeye={
      display: this.state.eye ? "none" : "block"
    }
    var hidecus={
      display: this.state.cus ? "none" : "block"
    }
    var hidehome={
      display: this.state.home ? "none" : "block"
    }
    return (
      <div className="app">
                    <button type="submit" class="btn btn-primary">1</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">2</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">3</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">4</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">5</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">6</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">7</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">8</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">9</button>&nbsp;&nbsp;
                    <button type="submit" class="btn btn-primary">10</button>&nbsp;&nbsp;
                    <text>{rand}</text>
     </div>
    );
  }
}

export default App;
